import { useState, useEffect } from 'react'
import { getBlogsDateData } from '../../../actions/admin'
import { getCookie } from '../../../actions/auth'
import { Line } from 'react-chartjs-2';
import Button from '@material-ui/core/Button'
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import moment from 'moment'
const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));
export default () => {
  const classes = useStyles();
  const [data, setData] = useState([])
  const [month_, setMonth] = React.useState(10);
  const [SelectedChartData, setSelectedChartData] = useState({ date: [], data: [] })
  const [chartDataThisMonth, setChartDataThisMonth] = useState({ date: [], data: [] })
  const [chartDataThisYear, setChartDataThisYear] = useState({ date: [], data: [] })
  const [chartDataAllTimes, setChartDataAllTimes] = useState({ date: [], data: [] })
  const token = getCookie('token')
  useEffect(() => {
    getBlogsDateData(token).then(res => {
      if (res.error) {
        return console.log(res.error)
      }
      console.log(res.data.filter(({ _id: { month } }) => month === month_))
      setData(res.data)
      setChartDataThisMonth({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('dddd')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })

      setChartDataThisYear({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('MMMM')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })
      setSelectedChartData({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('dddd')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })
    })
  }, [])
  return (
    <>
      <Line data={{
        labels: chartDataThisYear.date,
        datasets: [
          {
            label: 'Blogs dataset',
            fill: false,
            lineTension: 0.1,
            backgroundColor: 'rgba(75,192,192,0.4)',
            borderColor: 'rgba(75,192,192,1)',
            borderCapStyle: 'butt',
            borderDash: [],
            borderDashOffset: 0.0,
            borderJoinStyle: 'miter',
            pointBorderColor: 'rgba(75,192,192,1)',
            pointBackgroundColor: '#fff',
            pointBorderWidth: 1,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
            pointHoverBorderColor: 'rgba(220,220,220,1)',
            pointHoverBorderWidth: 2,
            pointRadius: 1,
            pointHitRadius: 10,
            data: chartDataThisYear.data
          }
        ]
      }} />
      <FormControl className={classes.formControl}>
        <InputLabel id="demo-simple-select-label">Age</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={month_}
          onChange={(event) => setMonth(parseInt(event.target.value))}
        >
          <MenuItem value={1}>Ten</MenuItem>
          <MenuItem value={2}>Twenty</MenuItem>
          <MenuItem value={3}>Thirty</MenuItem>
          <MenuItem value={4}>Ten</MenuItem>
          <MenuItem value={5}>Twenty</MenuItem>
          <MenuItem value={6}>Thirty</MenuItem>
          <MenuItem value={7}>Ten</MenuItem>
          <MenuItem value={8}>Twenty</MenuItem>
          <MenuItem value={9}>Thirty</MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={11}>Twenty</MenuItem>
          <MenuItem value={12}>Thirty</MenuItem>
        </Select>
      </FormControl>
      <Button size="small">This Year</Button>
      <Button size="small">all Time</Button>
    </>
  )
};
