  if (token) {
    return singleBlog(token, ctx.query.slug)
      .then(data => {
        if (data.error) {
          console.log(data.error);
        } else {
          return {
            blog: data, query: ctx.query
          };
        }
      });
  } else {
    return singleBlogPart(ctx.query.slug).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        return {
          blog: data, query: ctx.query
        };
      }
    });
  }

};
