import { useState, useEffect } from 'react'
import Head from 'next/head'
import Router, { withRouter } from 'next/router'
import Link from '../../src/Link'
import Layout from '../../components/Layout'
import { getCookie,isAuth } from '../../actions/auth'
import { singleBlog, listRelated, singleBlogPart } from "../../actions/blog";
import { API, DOMAIN, NAME, FB_APP_ID_ } from "../../config";
import { Cookie, withCookie } from 'next-cookie'
import moment from 'moment'
import renderHTML from 'react-render-html';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import { makeStyles } from '@material-ui/core/styles';
import DisqusThread from './DisqusThread'
import GradeIcon from '@material-ui/icons/Grade';
import Card from '../../components/blog/layout/Card'
import {arrayBufferToBase64} from '../../actions/helpers'
import Grid from '@material-ui/core/Grid';
import Hidden from '@material-ui/core/Hidden';
const useStyles = makeStyles(theme => ({
  root: { display: 'flex', flexDirection: 'column' },
  margin: {
    margin: theme.spacing(1),
  },
  extendedIcon: {
    marginRight: theme.spacing(1),
  },
  userInfo: {
    display: 'flex',
    justifyContent: 'flex-start',
    alignItems: 'center',
    marginBottom: 20
  },
  userPhotoHolder: {
    background: 'green',
    padding: 1,
    marginRight: 10,
    borderRadius: '50%',
    position: 'relative',
    zIndex: 99,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  userPhotoMain: {
    background: 'white',
    height: 65,
    width: 65,
    borderRadius: '50%',
    zIndex: 92,
    padding: 2,
    overflow: 'hidden',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  userProfilePhoto: {
    padding: 3,
    flex: 1,
    height: '100%',
    width: '100%',
    objectFit: 'cover',
    borderRadius: '50%'
  },
  userProfileExtra: {
    position: 'absolute',
    width: 70,
    height: 25,
    backgroundColor: 'white',
    zIndex: 90
  },
  mainImage: {
    height: 400,
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    overflow: 'hidden',
    marginBottom: 25,
    marginTop: 25
  },
  bodyHolder: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center'
  },
  LoginButton: {
    width: '50%',
    marginTop: 20
  },
  thumbUpHolder: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
  thumbUpLogo: {
    width: 35,
    height: 35,
    borderRadius: '50%',
    border: '1px solid lightgray',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 20,
    padding: 3,
  }
}));
const SinglePage = ({ blog, query }) => {
  const classes = useStyles();
  const [related, setRelated] = useState([]);
  const token = getCookie('token')
  const loadRelated = () => {
    listRelated({ _id: blog._id, categories: blog.categories }).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setRelated(data);
      }
    });
  };
  useEffect(() => {
    loadRelated();
  }, []);

  const head = () => (
    <Head>
      <title>
        {blog.title} | {NAME}
      </title>
      <meta name="description" content={blog.mdesc} />
      <link rel="canonical" href={`${DOMAIN}/blogs/${query.slug}`} />
      <meta property="og:title" content={`${blog.title}| ${NAME}`} />
      <meta property="og:description" content={blog.mdesc} />
      <meta property="og:type" content="webiste" />
      <meta property="og:url" content={`${DOMAIN}/blogs/${query.slug}`} />
      <meta property="og:site_name" content={`${NAME}`} />
      <meta property="og:image" content={`${API}/blog/photo/${blog.slug}`} />
      <meta property="og:image:secure_url" ccontent={`${API}/blog/photo/${blog.slug}`} />
      <meta property="og:image:type" content="image/jpg" />
      <meta property="fb:app_id" content={`${FB_APP_ID_}`} />
    </Head>
  );

  const showBlogCategories = blog => (
    <ButtonGroup size="small" aria-label="small outlined button group">
      {
        blog.categories.map((c, i) => (
          <Button key={i} onClick={() => Router.push(`/categories/${c.slug}`)}>
            {c.name}
          </Button>
        ))
      }
    </ButtonGroup>
  );

  const showBlogTags = blog =>(
    <ButtonGroup size="small" aria-label="small outlined button group">
      {
        blog.tags.map((c, i) => (
          <Button key={i} onClick={() => Router.push(`/tags/${c.slug}`)}>
            {c.name}
          </Button>
        ))
      }
    </ButtonGroup>
  )

  const showRelatedBlog = () => {
    return related.map((blog, i) => (
