import { useState, useEffect } from 'react'
import { getBlogsDateData } from '../../../actions/admin'
import { getCookie } from '../../../actions/auth'
import { Line } from 'react-chartjs-2';
import Button from '@material-ui/core/Button'
import moment from 'moment'
export default () => {
  const [data, setData] = useState([])
  const [SelectedChartData, setSelectedChartData] = useState({ date: [], data: [] })
  const [chartDataThisMonth, setChartDataThisMonth] = useState({ date: [], data: [] })
  const [chartDataThisYear, setChartDataThisYear] = useState({ date: [], data: [] })
  const [chartDataAllTimes, setChartDataAllTimes] = useState({ date: [], data: [] })
  const token = getCookie('token')
  useEffect(() => {
    getBlogsDateData(token).then(res => {
      if (res.error) {
        return console.log(res.error)
      }
      console.log(res.data)
      setData(res.data)
      setChartDataThisMonth({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('dddd')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })
      setChartDataThisYear({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('MMMM')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })
      setSelectedChartData({
        date: res.data.sort((a, b) => (a._id.day > b._id.day) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ _id: { year, month, day } }, i) => moment(`${year}-${month}-${day}T00:00:00`).format('dddd')),
        data: res.data.sort((a, b) => (a._id.day > b.last_nom) ? 1 : ((b._id.day > a._id.day) ? -1 : 0)).map(({ count }, i) => count)
      })
    })
  }, [])
  return (
    <>
      <Line data={{
        labels: chartDataThisYear.date,
        datasets: [
          {
            label: 'Blogs dataset',
            fill: false,
            lineTension: 0.1,
            backgroundColor: 'rgba(75,192,192,0.4)',
            borderColor: 'rgba(75,192,192,1)',
            borderCapStyle: 'butt',
            borderDash: [],
            borderDashOffset: 0.0,
            borderJoinStyle: 'miter',
            pointBorderColor: 'rgba(75,192,192,1)',
            pointBackgroundColor: '#fff',
            pointBorderWidth: 1,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
            pointHoverBorderColor: 'rgba(220,220,220,1)',
            pointHoverBorderWidth: 2,
            pointRadius: 1,
            pointHitRadius: 10,
            data: chartDataThisYear.data
          }
        ]
      }} />
      <Button size="small">This Month</Button>
      <Button size="small">This Year</Button>
      <Button size="small">all Time</Button>
    </>
  )
};
